        public ProductController controller;

        public void SetUp(RepositoryContext context, bool? isAdmin = false)
        {
            ILogger<ProductRepository> loggerProductRepo = new Mock<ILogger<ProductRepository>>().Object;

            IProductRepository productRepo = new ProductRepository(context, loggerProductRepo);

            IMapper mapper = new MapperConfiguration(cfg => cfg.AddProfile<ProductProfile>()).CreateMapper();

            string role = "Customer";
            if ((bool)isAdmin)
            {
                role = "Admin";
            }
            Claim roleClaim = new Claim("role", role);
            ClaimsIdentity identity = new ClaimsIdentity(new[] { roleClaim }, "BasicAuthentication");
            ClaimsPrincipal contextUser = new ClaimsPrincipal(identity);
            DefaultHttpContext httpContext = new DefaultHttpContext()
            {
                User = contextUser
            };
            HttpContextAccessor httpContextAccessor = new HttpContextAccessor()
            {
                HttpContext = httpContext
            };

            ILogger<Services.ProductService> loggerProductService = new Mock<ILogger<Services.ProductService>>().Object;

            IProductService productService = new Services.ProductService(mapper, productRepo, 
                                                                         httpContextAccessor, loggerProductService);

            ILogger<ProductController> loggerProductController = new Mock<ILogger<ProductController>>().Object;

            controller = new ProductController(productService, loggerProductController);
        }

        [Fact]
        public void CreateProduct_WhenCreatedByAdminWithValidProductInformation_Returns201WithIdentityResponseDto()
        {
            SetUp(TestHelper.GetContextWithRecords(), true);

            ProductCreateDto newProduct = new ProductCreateDto
            {
                Name = "Samsung Galaxy S23 Ultra (Black)",
                Price = 100000,
                Description = "2023 flagship of Samsung",
                Image = "",
                AvailableCount = 100,
                Visibility = true,
                Category = "Electronics and Gadgets"
            };

            IActionResult result = controller.CreateProduct(newProduct);

            ObjectResult createdResult = result.Should().BeOfType<ObjectResult>().Subject;
            createdResult.StatusCode.Should().Be(StatusCodes.Status201Created);
            createdResult.Value.Should().BeOfType<IdentityResponseDto>();

            IdentityResponseDto identityResponse = (IdentityResponseDto)createdResult.Value;
            IActionResult resultGet = controller.GetProductById(identityResponse.Id);

            ObjectResult okResult = resultGet.Should().BeOfType<ObjectResult>().Subject;
            okResult.StatusCode.Should().Be(StatusCodes.Status200OK);

            okResult.Value.Should().BeEquivalentTo(new ProductResponseDto
            {
                Id = identityResponse.Id,
                Name = "Samsung Galaxy S23 Ultra (Black)",
                Price = 100000,
                Description = "2023 flagship of Samsung",
                Image = "",
                AvailableCount = 100,
                Category = "Electronics and Gadgets"
            });
        }

        [Fact]
        public void CreateProduct_WhenCreatedByAdminWithExistingProductName_ThrowsConflictException()
        {
            SetUp(TestHelper.GetContextWithRecords(), true);

            ProductCreateDto newProduct = new ProductCreateDto
            {
                Name = "Google Pixel 7 Pro",
                Price = 75700,
                Description = "The Pixel 7 Pro triple rear camera system includes a 5x telephoto lens and has a 30x Super Res Zoom for high-quality photos from far away.",
                Image = "",
                AvailableCount = 100,
                Visibility = true,
                Category = "Electronics and Gadgets"
            };

            Action act = () => controller.CreateProduct(newProduct);

            act.Should().Throw<ConflictException>().WithMessage("Product name already exists");
        }

