        [Fact]
        public void UpdateProduct_WhenCalledByAdminWithValidProductInformation_Returns200()
        {
            SetUp(TestHelper.GetContextWithRecords(), true);

            ProductUpdateDto updatedProduct = new ProductUpdateDto
            {
                Name = "Samsung Galaxy S23 Ultra (8/256, Graphite)",
                Price = 149999,
                Visibility = true,
            };

            IActionResult resultUpdateProduct = controller.UpdateProduct(Guid.Parse("123ff65c-e52d-47e7-b409-18ef76672edc"), updatedProduct);

            IActionResult resultGetProduct = controller.GetProductById(Guid.Parse("123ff65c-e52d-47e7-b409-18ef76672edc"));

            StatusCodeResult okResultUpdate = resultUpdateProduct.Should().BeOfType<StatusCodeResult>().Subject;
            okResultUpdate.StatusCode.Should().Be(StatusCodes.Status200OK);

            ObjectResult okResultGet = resultGetProduct.Should().BeOfType<ObjectResult>().Subject;
            okResultGet.Value.Should().BeEquivalentTo(new ProductResponseDto
            {
                Id = Guid.Parse("123ff65c-e52d-47e7-b409-18ef76672edc"),
                Name = "Samsung Galaxy S23 Ultra (8/256, Graphite)",
                Price = 149999,
                Description = "2023 flagship of Samsung",
                Image = "",
                AvailableCount = 100,
                Category = "Electronics and Gadgets"
            });
        }

