using Entities.Models;
using Microsoft.EntityFrameworkCore;
using Repository;
using System.Text;

namespace ProductServiceTests
{
    public class TestHelper
    {
        /// <summary>
        /// Seeds the in memory database with records
        /// </summary>
        /// <returns></returns>
        public static RepositoryContext GetContextWithRecords()
        {
            var option = new DbContextOptionsBuilder<RepositoryContext>().UseInMemoryDatabase(databaseName: Guid.NewGuid().ToString()).Options;

            var context = new RepositoryContext(option);

            if (context != null)
            {
                context.Database.EnsureDeleted();
                context.Database.EnsureCreated();
            }

            context.Add(new Product()
            {
                Id = Guid.Parse("112ff65c-e52d-47e7-b409-18ef76672edc"),
                Name = "Google Pixel 7 Pro",
                Price = 79999,
                Description = "The Pixel 7 Pro triple rear camera system includes a 5x telephoto lens and has a 30x Super Res Zoom for high-quality photos from far away.",
                Image = Convert.FromBase64String(""),
                AvailableCount = 100,
                Visibility = true,
                Category = "Electronics and Gadgets"
            });

            context.Add(new Product()
            {
                Id = Guid.Parse("e3500bd6-278e-4171-b0f1-6f2233b340b2"),
                Name = "Puma Electron E Unisex Shoes",
                Price = 30999,
                Description = "Unisex casual shoes from Puma",
                Image = Convert.FromBase64String(""),
                AvailableCount = 100,
                Visibility = true,
                Category = "Footwear and Shoes"
            });

            context.Add(new Product()
            {
                Id = Guid.Parse("123ff65c-e52d-47e7-b409-18ef76672edc"),
                Name = "Samsung Galaxy S23 Ultra",
                Price = 150000,
                Description = "2023 flagship of Samsung",
                Image = Convert.FromBase64String(""),
                AvailableCount = 100,
                Visibility = false,
                Category = "Electronics and Gadgets"
            });

            context.SaveChanges();

            return context;
        }

        /// <summary>
        /// Seeds the in memory database with no records
        /// </summary>
        /// <returns></returns>
        public static RepositoryContext GetEmptyContext()
        {
            var option = new DbContextOptionsBuilder<RepositoryContext>().UseInMemoryDatabase(databaseName: Guid.NewGuid().ToString()).Options;

            var context = new RepositoryContext(option);

            if (context != null)
            {
                context.Database.EnsureDeleted();
                context.Database.EnsureCreated();
            }

            return context;
        }

    }
}
